import base64
import datetime
import hashlib
import random
import re
import xml.etree.ElementTree as ET

from streamlink.compat import urlencode
from streamlink.plugin import Plugin
from streamlink.stream import HLSStream


class Radiko(Plugin):
    _url_re = re.compile(r'http://radiko\.jp/(#!/)?(?P<state>live|ts)/(?P<station_id>[a-zA-Z0-9-]+)/?(?P<start_at>\d+)?')
    _api_auth_1 = 'https://radiko.jp/v2/api/auth1'
    _api_auth_2 = 'https://radiko.jp/v2/api/auth2'
    _fullKey = ""
    _location_list = {"JP1": [43.064615, 141.346807], "JP2": [40.824308, 140.739998], "JP3": [39.703619, 141.152684], "JP4": [38.268837, 140.8721], "JP5": [39.718614, 140.102364], "JP6": [38.240436, 140.363633], "JP7": [37.750299, 140.467551], "JP8": [36.341811, 140.446793], "JP9": [36.565725, 139.883565], "JP10": [36.390668, 139.060406], "JP11": [35.856999, 139.648849], "JP12": [35.605057, 140.123306], "JP13": [35.689488, 139.691706], "JP14": [35.447507, 139.642345], "JP15": [37.902552, 139.023095], "JP16": [36.695291, 137.211338], "JP17": [36.594682, 136.625573], "JP18": [36.065178, 136.221527], "JP19": [35.664158, 138.568449], "JP20": [36.651299, 138.180956], "JP21": [35.391227, 136.722291], "JP22": [34.97712, 138.383084], "JP23": [35.180188, 136.906565], "JP24": [34.730283, 136.508588], "JP25": [35.004531, 135.86859], "JP26": [35.021247, 135.755597], "JP27": [34.686297, 135.519661], "JP28": [34.691269, 135.183071], "JP29": [34.685334, 135.832742], "JP30": [34.225987, 135.167509], "JP31": [35.503891, 134.237736], "JP32": [35.472295, 133.0505], "JP33": [34.661751, 133.934406], "JP34": [34.39656, 132.459622], "JP35": [34.185956, 131.470649], "JP36": [34.065718, 134.55936], "JP37": [34.340149, 134.043444], "JP38": [33.841624, 132.765681], "JP39": [33.559706, 133.531079], "JP40": [33.606576, 130.418297], "JP41": [33.249442, 130.299794], "JP42": [32.744839, 129.873756], "JP43": [32.789827, 130.741667], "JP44": [33.238172, 131.612619], "JP45": [31.911096, 131.423893], "JP46": [31.560146, 130.557978], "JP47": [26.2124, 127.680932]}
    _station_region = {"HBC" : "JP1", "STV" : "JP1", "RN1" : "JP1", "RN2" : "JP1", "AIR-G" : "JP1", "NORTHWAVE" : "JP1", "HOUSOU-DAIGAKU" : "JP1", "JOIK" : "JP1", "JOAK-FM" : "JP1", "RAB" : "JP2", "AFB" : "JP2", "JOHK" : "JP2", "IBC" : "JP3", "FMI" : "JP3", "TBC" : "JP4", "DATEFM" : "JP4", "ABS" : "JP5", "AFM" : "JP5", "YBC" : "JP6", "RFM" : "JP6", "RFC" : "JP7", "FMF" : "JP7", "TBS" : "JP8", "QRR" : "JP8", "LFR" : "JP8", "INT" : "JP8", "FMT" : "JP8", "FMJ" : "JP8", "IBS" : "JP8", "JORF" : "JP8", "BAYFM78" : "JP8", "NACK5" : "JP8", "YFM" : "JP8", "JOAK" : "JP8", "CRT" : "JP9", "RADIOBERRY" : "JP9", "FMGUNMA" : "JP10", "BSN" : "JP15", "FMNIIGATA" : "JP15", "KNB" : "JP16", "FMTOYAMA" : "JP16", "JOCK" : "JP16", "MRO" : "JP17", "HELLOFIVE" : "JP17", "FBC" : "JP18", "FMFUKUI" : "JP18", "YBS" : "JP19", "FM-FUJI" : "JP19", "SBC" : "JP20", "FMN" : "JP20", "CBC" : "JP21", "TOKAIRADIO" : "JP21", "GBS" : "JP21", "ZIP-FM" : "JP21", "FMGIFU" : "JP21", "FMAICHI" : "JP21", "SBS" : "JP22", "K-MIX" : "JP22", "FMMIE" : "JP24", "ABC" : "JP25", "MBS" : "JP25", "OBC" : "JP25", "CCL" : "JP25", "802" : "JP25", "FMO" : "JP25", "KBS" : "JP25", "KISSFMKOBE" : "JP25", "E-RADIO" : "JP25", "JOBK" : "JP25", "ALPHA-STATION" : "JP26", "CRK" : "JP26", "WBS" : "JP30", "BSS" : "JP31", "FM-SANIN" : "JP31", "JOFK" : "JP31", "RSK" : "JP33", "FM-OKAYAMA" : "JP33", "RCC" : "JP34", "HFM" : "JP34", "KRY" : "JP35", "FMY" : "JP35", "JRT" : "JP36", "JOZK" : "JP36", "RNC" : "JP37", "FMKAGAWA" : "JP37", "RNB" : "JP38", "JOEU-FM" : "JP38", "RKC" : "JP39", "HI-SIX" : "JP39", "RKB" : "JP40", "KBC" : "JP40", "LOVEFM" : "JP40", "CROSSFM" : "JP40", "FMFUKUOKA" : "JP40", "JOLK" : "JP40", "FMS" : "JP41", "NBC" : "JP42", "FMNAGASAKI" : "JP42", "RKK" : "JP43", "FMK" : "JP43", "OBS" : "JP44", "FM_OITA" : "JP44", "MRT" : "JP45", "JOYFM" : "JP45", "MBC" : "JP46", "MYUFM" : "JP46", "RBC" : "JP47", "ROK" : "JP47", "FM_OKINAWA" : "JP47"}

    @classmethod
    def can_handle_url(cls, url):
        return cls._url_re.match(url)

    def _get_streams(self):
        match = self._url_re.match(self.url)
        state = match.group('state')
        station_id = match.group('station_id').upper()
        if state == 'live':
            url, token = self._live(station_id)
        else:
            start_at = match.group('start_at')
            url, token = self._timefree(station_id, start_at)
        headers = {
            'X-Radiko-AuthToken': token
        }
        self.session.http.headers = headers
        for s in HLSStream.parse_variant_playlist(self.session, url).items():
            yield s

    def _live(self, station_id):
        live_url = 'http://f-radiko.smartstream.ne.jp/{}/_definst_/simul-stream.stream/playlist.m3u8'.format(station_id)
        token, area_id = self._authorize(station_id)
        lsid = hashlib.md5(str(random.random()).encode('utf-8')).hexdigest()
        live_params = {
            'station_id': station_id,
            'l': 15,
            'lsid': lsid,
            'type': 'b'
        }
        url = live_url + '?' + urlencode(live_params)
        return url, token

    def _timefree(self, station_id, start_at):
        m3u8_url = 'https://radiko.jp/v2/api/ts/playlist.m3u8'
        token, area_id = self._authorize(station_id)
        lsid = hashlib.md5(str(random.random()).encode('utf-8')).hexdigest()
        end_at = self._get_xml(start_at, station_id)
        m3u8_params = {
            'station_id': station_id,
            'start_at': start_at,
            'ft': start_at,
            'end_at': end_at,
            'to': end_at,
            'l': 15,
            'lsid': lsid,
            'type': 'b'
        }
        url = m3u8_url + '?' + urlencode(m3u8_params)
        return url, token

    def _authorize(self, station_id):
        region_id = self._station_region[station_id]
        location = self._location_list[region_id]
        headers = {
            'x-radiko-app': 'aSmartPhone7a',
            'x-radiko-app-version': '0.0.1',
            'x-radiko-device': 'Python.StreamLink',
            'x-radiko-user': 'dummy_user'
        }
        self.session.http.headers.update(headers)
        r = self.session.http.get(self._api_auth_1)
        token = r.headers.get('x-radiko-authtoken')
        offset = int(r.headers.get('x-radiko-keyoffset'))
        length = int(r.headers.get('x-radiko-keylength'))
        partial_key = base64.b64encode(base64.b64decode(self._fullKey)[offset:offset + length]).decode('utf-8')
        headers = {
            'x-radiko-authtoken': token,
            'x-radiko-device': 'Python.StreamLink',
            'x-radiko-partialkey': partial_key,
            'x-radiko-user': 'dummy_user',
            'x-radiko-connection': 'wifi',
            'x-radiko-location': str(location[0])+','+str(location[1])+',gps'
        }
        self.session.http.headers.update(headers)
        r = self.session.http.get(self._api_auth_2)
        if r.status_code == 200:
            return token, r.text.split(',')[0]

    def _get_xml(self, start_at, station_id):
        today = datetime.date(int(start_at[:4]), int(start_at[4:6]), int(start_at[6:8]))
        yesterday = today - datetime.timedelta(days=1)
        if int(start_at[8:10]) < 5:
            date = yesterday.strftime('%Y%m%d')
        else:
            date = today.strftime('%Y%m%d')
        api = 'http://radiko.jp/v3/program/station/date/{}/{}.xml'.format(date, station_id)
        r = self.session.http.get(api)
        tree = ET.XML(r.content)
        for x in tree[2][0][1].findall('prog'):
            if x.attrib['ft'] == start_at:
                return x.attrib['to']


__plugin__ = Radiko
